
public class swapNum {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num1 = 25;
		int num2 = 30;

		num1 = num1 + num2;
		num2 = num1 - num2;
		num1 = num1 - num2;

		System.out.println("num1= " + num1);
		System.out.println("num2= " + num2);
	}

}
